INSERT INTO `blog_description` VALUES (65,1,'Достойное прощание с умершим','&lt;p&gt;Квалифицированная подготовка тела&lt;/p&gt;\r\n\r\n&lt;p&gt;на базе собственных моргов;&lt;/p&gt;\r\n\r\n&lt;p&gt;Собственные ритуальные залы&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n','http://localhost:8888/index.php?route=page/article&amp;article_id=67_70','FFFFFF'),(66,1,'Транспортировка и погребение','&lt;p&gt;Любое кладбище города и области, собственный спецтранспорт, кремация, отправка умершего за границу&lt;/p&gt;\r\n','http://localhost:8888/index.php?route=page/article&amp;article_id=67_70','FFFFFF'),(67,1,'Чуткий подход и понимание','&lt;p&gt;Круглосуточный выезд, штат квалифицированных сотрудников, гибкая система скидок, рассрочка платежа, прижизненный договор&lt;/p&gt;\r\n','http://ярритуалсервис.рф/otzyvy','FFFFFF'),(68,1,'Памятники','&lt;p&gt;Изготовление и установка памятников, оград. На сайте вы можете самостоятельно рассчитать стоимость памятника!&lt;/p&gt;\r\n','http://ярритуалсервис.рф/pamjatniki/','FFFFFF'),(69,1,'Скидка 10% на изготовление памятника с художественным оформлением','&lt;p&gt;При заказе памятника в течение 3 месяца после похорон посредством организации ЯрРитуал Сервис&lt;/p&gt;\r\n','http://ярритуалсервис.рф/pamjatniki/skidka-10-procentov/','FFFFFF'),(71,1,'Венки к 70-летию Победы','&lt;p&gt;Принимаем заказы на изготовление венков с символикой и написание лент ко Дню Победы&lt;/p&gt;\r\n','http://ярритуалсервис.рф/pobeda/','FFFFFF'),(70,1,'Благоустройство захоронений','&lt;p&gt;Комплексное благоустройство захоронений в Ярославле, высокое качество работы.&lt;/p&gt;\r\n','http://ярритуалсервис.рф/index.php?route=page/article&amp;article_id=67_75','FFFFFF'),(64,1,'Организация прощаний и похорон','&lt;p&gt;Похоронный дом &amp;laquo;ЯрРитуал Сервис&amp;raquo; &amp;mdash; крупное специализированное предприятие по вопросам похоронного дела г. Ярославля&lt;/p&gt;\r\n','http://ярритуалсервис.рф/ritualnye-uslugi','FFFFFF'),(72,1,'Открытие нового офиса